mapper.update:
UPDATE 
	t_project_plan_tracking 
SET 
	id = id

		, name = #{name}


		, project = #{project}


		, project_level = #{projectLevel}


		, plan_start_time = #{planStartTime}


		, plan_end_time = #{planEndTime}


		, description = #{description}


		, task_id = #{taskId}


		, user_id = #{userId}


		, user_name = #{userName}


		, lifecycle = #{lifecycle}

where 
	id = #{id}
	and lifecycle != 21

mapper.select:
SELECT id, name, project, project_level projectLevel, plan_start_time planStartTime, plan_end_time planEndTime, description, task_id taskId, user_id userId, user_name userName, lifecycle, create_time createTime, update_time updateTime from t_project_plan_tracking where lifecycle != 21
and id = #{id}
and name like CONCAT('%', #{name}, '%')
and project = #{project}
and plan_start_time >= #{planStartTime}
and project_level = #{projectLevel}
and plan_start_time <= #{planEndTime}
and task_id = #{taskId}
and user_id = #{userId}
and lifecycle = #{lifecycle}
order by id desc
