mapper.update:
		UPDATE t_task
		SET code = code
		
			, title = #{title}
		
		
			, project = #{project}
		
		
			, tags = #{tags}
		
		
			, is_parent = #{isParent}
		
		
			, parent_id = #{parentId}
		
		
			, description = #{description}
		
		
			, plan_score = #{planScore}
		
		
			, plan_status = #{planStatus}
		
		
			, plan_hours = #{planHours}
		
		
			, status = #{status}
		
		
			, create_by = #{createBy}
		
		
			, update_by = #{updateBy}
		
		
			, percent_hours = #{percentHours}
		

		where id=#{id}

mapper.select:
SELECT id id, CODE CODE, title title, project project, tags tags, is_parent isParent, parent_id parentId, description description, plan_score planScore, plan_status planStatus, plan_hours planHours, status status, create_by createBy, create_time createTime, update_by updateBy, update_time updateTime, percent_hours percentHours FROM t_task WHERE 1=1 and status != 21
and id = #{searchParams.id}
and status = #{searchParams.status}
and is_parent = #{searchParams.isParent}
and parent_id = #{searchParams.parentId}

SELECT COUNT(tt1.id) FROM t_task tt1 ,t_task_week tw1 WHERE tt1.status != 21
AND tw1.user_id IN
#{item}
AND DATE_FORMAT(tt1.create_time,'%Y-%m')=DATE_FORMAT(#{searchParams.time},'%Y-%m')
AND tt1.id = tw1.task_id AND( ( tt1.is_parent IS NULL AND tt1.parent_id IS NULL
AND (tt1.tags LIKE '%,${searchParams.tag},%' OR tt1.tags LIKE '%,${searchParams.tag}' OR tt1.tags LIKE '${searchParams.tag},%' OR tt1.tags = ${searchParams.tag})
) OR ( tt1.is_parent = 1 AND EXISTS(SELECT 1 FROM t_task tt2 ,t_task_week tw2 WHERE tt2.status != 21 AND tt2.id = tw2.task_id AND tt2.plan_status = tw2.current_status AND tt1.id = tt2.parent_id
AND (tt1.tags LIKE '%,${searchParams.tag},%' OR tt1.tags LIKE '%,${searchParams.tag}' OR tt1.tags LIKE '${searchParams.tag},%' OR tt1.tags = ${searchParams.tag})
) ) )

SELECT tw1.id taskWeekId, tt1.id taskId, tt1.CODE CODE, tt1.title title, tt1.parent_id parentId, tt1.is_parent isParent, tt1.project project, tt1.tags tags, tt1.description description, tt1.plan_score planScore, tw1.actual_score actualScore, tt1.plan_status planStatus, tw1.current_status currentStatus, DATE_FORMAT(tw1.end_time,'%Y-%m-%d') endTime, tt1.plan_hours planHours, tw1.actual_hours actualHours, tw1.user_id userId, tw1.YEAR YEAR, tw1.WEEK WEEK, tw1.MONTH MONTH, tw1.create_by createBy, tw1.create_time createTime, tw1.update_by updateBy, tw1.update_time updateTime, tw1.user_name userName, tt1.percent_hours percentHours, tw1.level FROM t_task tt1 ,t_task_week tw1 WHERE tt1.status != 21
AND tw1.user_id IN
#{item}
AND DATE_FORMAT(tt1.create_time,'%Y-%m')=DATE_FORMAT(#{searchParams.time},'%Y-%m')
AND tt1.id = tw1.task_id AND( ( tt1.is_parent IS NULL AND tt1.parent_id IS NULL
AND (tt1.tags LIKE '%,${searchParams.tag},%' OR tt1.tags LIKE '%,${searchParams.tag}' OR tt1.tags LIKE '${searchParams.tag},%' OR tt1.tags = ${searchParams.tag})
) OR ( tt1.is_parent = 1 AND EXISTS(SELECT 1 FROM t_task tt2 ,t_task_week tw2 WHERE tt2.status != 21 AND tt2.id = tw2.task_id AND tt2.plan_status = tw2.current_status AND tt1.id = tt2.parent_id
AND (tt1.tags LIKE '%,${searchParams.tag},%' OR tt1.tags LIKE '%,${searchParams.tag}' OR tt1.tags LIKE '${searchParams.tag},%' OR tt1.tags = ${searchParams.tag})
) ) )
